import { FC, useEffect, useState } from "react";
import List from "../../components/List/List";
import { ICars } from "../../types/types";
import axios from "axios";
import { ThreeDots } from "react-loader-spinner";
import "./index.css";
import createCardData from "../../helpers/createCarsData";

export default function Main() {
  const [cars, setCars] = useState<ICars[]>(() => JSON.parse(localStorage.getItem('cars') || '[]'));
  const [selectedCategory, setSelectedCategory] = useState('none')

  const getApiData = async () => {
    try {
      const { data } = await axios.get("http://localhost:3001/cars");
      const carsData = createCardData(data)
    } catch (error) {
      console.log(error);
    }
  };

  let sortedCarsDesc = (selectedCategory: string) => {
    setSelectedCategory(selectedCategory)
    const sortedData = [...cars];
      if (selectedCategory === 'Sort by year') {
        sortedData.sort((a,b) => Number(b.year) - Number(a.year))
      } else if (selectedCategory === 'Sort by price') {
        sortedData.sort((a,b) => Number(b.price) - Number(a.price))
      }
      setCars(sortedData)
  }

  let sortedCarsAsc = (selectedCategory: string) => {
    setSelectedCategory(selectedCategory)
    const sortedData = [...cars];
      if (selectedCategory === 'Sort by year') {
        sortedData.sort((a,b) => Number(a.year) - Number(b.year))
      } else if (selectedCategory === 'Sort by price') {
        sortedData.sort((a,b) => Number(a.price) - Number(a.price))
      }
      setCars(sortedData)
  }



  useEffect(() => {

    getApiData();
    return () => {
      localStorage.setItem('CarState', JSON.stringify(cars))
    }
  }, [cars, selectedCategory]);

  return (
    <div className="container">
      {cars.length > 0 ? (
      <div className="list"> 
      <div className="sort-block">
      <label>Sort in descending order </label> 
          <select value={selectedCategory} onChange={(e) => sortedCarsDesc(e.target.value)}>
             <option value="none">None</option>
            <option value="Sort by year" >Year</option>
            <option value="Sort by price">Price</option>
          </select>
          <label>Sort in ascending order </label> 
          <select value={selectedCategory} onChange={(e) => sortedCarsAsc(e.target.value)}>
             <option value="none">None</option>
            <option value="Sort by year" >Year</option>
            <option value="Sort by price">Price</option>
          </select>
          </div>
{/*           <button>Sort by year</button>
          <button>Sort by price</button> */}   
       <List data={cars}/>
       </div>
      ) : (
        <div className="loader">
        <ThreeDots 
          height="80" 
          width="80" 
          radius="9"
          color="#7dc2d8" 
          ariaLabel="three-dots-loading"
          wrapperStyle={{}}
          visible={true}
          />
       </div> 
      )}
    </div>
  );
}
