import { FC, useEffect, useState } from "react";
import List from "../../components/List/List";
import { ICars } from "../../types/types";
import axios from "axios";
import { ThreeDots } from "react-loader-spinner";
import "./index.css";
import createCardData from "../../helpers/createCarsData";

export default function Main() {
  const [cars, setCars] = useState<ICars[]>(() => JSON.parse(localStorage.getItem('cars') || '[]'));
  const [selectedCategory, setSelectedCategory] = useState('')

  const getApiData = async () => {
    try {
      const { data } = await axios.get("http://localhost:3001/cars");
      const carsData = createCardData(data)
    } catch (error) {
      console.log(error);
    }
  };

  let sortedCars = (selectedCategory: string) => {
    const sortedData = [...cars];
      if (selectedCategory === 'Sort by year') {
        sortedData.sort((a,b) => a.price - a.price)
      } else if (selectedCategory === 'Sort by price') {
        sortedData.sort((a,b) => b.price - a.price)
      }
      setCars(sortedData)
  }



  useEffect(() => {

    getApiData();
    return () => {
      localStorage.setItem('CarState', JSON.stringify(cars))
    }
  }, [cars]);

  return (
    <div className="container">
      {cars.length > 0 ? (
      <div className="list">  
          <select value={selectedCategory} onChange={(e) => sortedCars(e.target.value)}>
            <option value="Sort by year">Year</option>
            <option value="Sort by year">Price</option>
          </select>
{/*           <button>Sort by year</button>
          <button>Sort by price</button> */}   
       <List data={cars}/>
       </div>
      ) : (
        <div className="loader">
        <ThreeDots 
          height="80" 
          width="80" 
          radius="9"
          color="#7dc2d8" 
          ariaLabel="three-dots-loading"
          wrapperStyle={{}}
          visible={true}
          />
       </div> 
      )}
    </div>
  );
}
