import { FC, useEffect, useState } from "react";
import List from "../../components/List/List";
import { ICars } from "../../types/types";
import axios from "axios";
import { ThreeDots } from "react-loader-spinner";
import "./index.css";
import createCardData from "../../helpers/createCarsData";

export default function Main() {
  const [cars, setCars] = useState<ICars[]>([
    /* {
    id: 0,
    brand: '',
    model: '',
    color: '',
    year: '',
    engine: '',
    transmission: '',
    pedalReserve: ''} */
  ]);

  const getApiData = async () => {
    try {
      const { data } = await axios.get("http://localhost:3001/cars");
      const slicedData = data.Results.slice(0, 10);
      console.log()
      const carsData = createCardData(slicedData)
      setCars(carsData);
    } catch (error) {
      console.log(error);
    }
  };

  console.log(cars, "!!!!!!!!!!!!!");

  useEffect(() => {
    getApiData();
  }, []);

  return (
    <div className="container">
      {cars.length > 0 ? (
      <div className="list">       
       <List data={cars}/>
       </div>
      ) : (
        <div className="loader">
        <ThreeDots 
          height="80" 
          width="80" 
          radius="9"
          color="#4fa94d" 
          ariaLabel="three-dots-loading"
          wrapperStyle={{}}
          visible={true}
          />
       </div> 
      )}
    </div>
  );
}
