import { FC, useEffect, useState } from "react";
import List from "../../components/List/List";
import { ICars } from "../../types/types";
import axios from "axios";
import { ThreeDots } from "react-loader-spinner";
import "./index.css";
import createCardData from "../../helpers/createCarsData";
import Navbar from "../../components/Navbar/Navbar";

export default function Main() {
  const [cars, setCars] = useState<ICars[]>([]);
  const [carsAreChanged, setCarsAreChanged] = useState(false)

  const savedState = localStorage.getItem('CarState');
  if (savedState) {
    setCars(JSON.parse(savedState));
  }


  const getApiData = async () => {
    try {
      const { data } = await axios.get("http://localhost:3001/cars");
      const carsData = createCardData(data)
      setCars(carsData);
    } catch (error) {
      console.log(error);
    }
  };

/*   console.log(cars, "!!!!!!!!!!!!!"); */

  useEffect(() => {

    getApiData();
    return () => {
      localStorage.setItem('CarState', JSON.stringify(cars))
    }
  }, [savedState]);

  return (
    <div className="container">
      {cars.length > 0 ? (
      <div className="list">       
        <header className="header">
          <Navbar />
        </header>
       <List data={cars}/>
       </div>
      ) : (
        <div className="loader">
        <ThreeDots 
          height="80" 
          width="80" 
          radius="9"
          color="#7dc2d8" 
          ariaLabel="three-dots-loading"
          wrapperStyle={{}}
          visible={true}
          />
       </div> 
      )}
    </div>
  );
}
